sprites.onOverlap(SpriteKind.Player, SpriteKind.NPC, function (sprite, otherSprite) {
    if (controller.B.isPressed() && talk1 == 0) {
        game.setDialogTextColor(7)
        game.showLongText("Hey Boss~", DialogLayout.Bottom)
        game.showLongText("Welcome to your new factory", DialogLayout.Bottom)
        game.showLongText("You need to fix up the new solar panels", DialogLayout.Bottom)
        solar_panels.startEffect(effects.spray)
    } else if (controller.B.isPressed() && talk1 == 1) {
        game.setDialogTextColor(7)
        game.showLongText("Well done!", DialogLayout.Bottom)
        game.showLongText("Now can you help me to fix up the rain droplet collector", DialogLayout.Bottom)
        Rain_Droplet_Collector.startEffect(effects.spray)
    } else if (controller.B.isPressed() && talk1 == 2) {
        game.showLongText("Good jod!", DialogLayout.Bottom)
        game.showLongText("Now you can going to the factory", DialogLayout.Bottom)
        game.showLongText("The password is \"0422\"", DialogLayout.Bottom)
        factory.sayText("I'm factory", 2000, false)
        password += 1
    } else if (controller.B.isPressed() && talk1 == 3) {
        game.showLongText("Welcome to the factory~", DialogLayout.Bottom)
        game.showLongText("You can see that machine is already to start", DialogLayout.Bottom)
        game.showLongText("But it still have some system program error...", DialogLayout.Bottom)
        if (game.ask("Can you help us to ", "check it up?")) {
            game.showLongText("Thank you boss", DialogLayout.Bottom)
            game.showLongText("We know that you are the best :)", DialogLayout.Bottom)
        }
    } else if (controller.B.isPressed() && talk1 == 4) {
        game.setDialogTextColor(6)
        jerry.sayText("Jerry", 500, false)
        game.showLongText("Welcome back to factory~ boss", DialogLayout.Bottom)
        game.showLongText("WOW!you have fixed the system", DialogLayout.Bottom)
        game.showLongText("WOW!you have fixed the system", DialogLayout.Bottom)
        game.setDialogTextColor(4)
        game.showLongText("Supercalifragilisticexpi alidocious!!!", DialogLayout.Bottom)
        game.setDialogTextColor(6)
        game.showLongText("If you wanna restart the machine", DialogLayout.Bottom)
        game.showLongText("You need to import the energy (waste heat gas) to computer", DialogLayout.Bottom)
    } else if (controller.B.isPressed() && talk1 == 5) {
        game.showLongText("Hey boss~", DialogLayout.Bottom)
        game.showLongText("I got a call that he says the government official is coming", DialogLayout.Bottom)
        game.showLongText("He's been waiting outside", DialogLayout.Bottom)
        game.showLongText("Let's go", DialogLayout.Bottom)
        talk1 += 1
    }
    jerry.sayText("B", 100, false)
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.computer, function (sprite, otherSprite) {
    machine2.sayText("B", 500, false)
    if (controller.B.isPressed() && game2 == 3) {
        game.setDialogTextColor(2)
        game.showLongText("Error...system can not keep up", DialogLayout.Bottom)
        game.showLongText("The temperature was too high", DialogLayout.Bottom)
        game.showLongText("Please check up the waste heat wheater too high or not", DialogLayout.Bottom)
        game.splash("Information", "What is \"waste heat\"?")
        game.setDialogTextColor(6)
        game.showLongText("Waste heat is heat that is produced by a machine", DialogLayout.Bottom)
        game.showLongText("or other process that uses energy", DialogLayout.Bottom)
        game.showLongText("If we can recycle the waste heat ", DialogLayout.Bottom)
        game.showLongText("That might be useful", DialogLayout.Bottom)
        if (game.ask("Do you wanna", "going to setting the system?")) {
            sprites.destroyAllSpritesOfKind(SpriteKind.NPC)
            sprites.destroyAllSpritesOfKind(SpriteKind.machine0)
            machine1.setPosition(80, 55)
            mainplayer.setPosition(80, 15)
            settings2 = 0
            item1 = sprites.create(assets.image`我的影像5`, SpriteKind.item)
            item2 = sprites.create(assets.image`我的影像6`, SpriteKind.item2)
            item3 = sprites.create(assets.image`我的影像7`, SpriteKind.item3)
            statusbar = statusbars.create(30, 4, StatusBarKind.Magic)
            statusbar.attachToSprite(machine1, 2, 0)
            statusbar.setColor(6, 0)
            statusbar.positionDirection(CollisionDirection.Top)
            statusbar.value = 0
            item1.setPosition(randint(10, 50), randint(20, 80))
            item2.setPosition(randint(100, 120), randint(20, 80))
            item3.setPosition(randint(120, 150), randint(20, 80))
            game2 += 1
        }
    }
    if (game2 == 4 && settings2 == 0) {
        machine1.sayText("B", 500, false)
        if (controller.B.isPressed()) {
            game.showLongText("You need to pick up those functions pack and take back for me", DialogLayout.Bottom)
            game.showLongText("I will start to creating new system of recycling waste heat", DialogLayout.Bottom)
            game.showLongText("Keep going~", DialogLayout.Bottom)
            settings2 += 1
        }
    }
    if (game2 == 4 && settings2 > 3) {
        machine1.sayText("B", 500, false)
        if (controller.B.isPressed()) {
            game.showLongText("Well done~", DialogLayout.Bottom)
            game.showLongText("Now Let's go to capture the waste heat", DialogLayout.Bottom)
            game.splash("If the waste heat close to you", "Press \"A\" to catch to gas")
            game.splash("Catch more gas", "as you can")
            if (game.ask("Are you ready ", "to start?")) {
                machine1.destroy()
                mainplayer.setPosition(80, 100)
                gas1 = sprites.create(assets.image`我的影像8`, SpriteKind.gas)
                line = sprites.create(assets.image`0`, SpriteKind.line)
                line.setPosition(80, 120)
                gas1.setPosition(randint(20, 120), 0)
                gas1.follow(line, randint(140, 180))
                info.startCountdown(40)
                music2 += 1
                game2 += 1
            }
        }
    }
    if (game2 == 6) {
        if (controller.B.isPressed() && talk1 == 4) {
            if (game.ask("Do you wanna import \"" + info.score() + "\" pt" + "", "gas into the machine?")) {
                if (info.score() >= 0 && info.score() < 20) {
                    turtle_boss += 1
                    game.showLongText("You have imported" + info.score() + " gas into the machine", DialogLayout.Bottom)
                    while (info.score() > 0) {
                        info.changeScoreBy(-1)
                    }
                    game.showLongText("Back to talk to Jerry", DialogLayout.Bottom)
                    talk1 += 1
                }
                if (info.score() >= 20 && info.score() < 50) {
                    rabbit_boss += 1
                    game.showLongText("You have imported" + info.score() + " gas into the machine", DialogLayout.Bottom)
                    while (info.score() > 0) {
                        info.changeScoreBy(-1)
                    }
                    game.showLongText("Back to talk to Jerry", DialogLayout.Bottom)
                    talk1 += 1
                }
                if (info.score() >= 50) {
                    game.showLongText("You have imported" + info.score() + " gas into the machine", DialogLayout.Bottom)
                    perfect_boss += 1
                    while (info.score() > 0) {
                        info.changeScoreBy(-1)
                    }
                    game.showLongText("Back to talk to Jerry", DialogLayout.Bottom)
                    talk1 += 1
                }
            }
        } else if (controller.B.isPressed() && talk1 == 5) {
            game.showLongText("Back to talk to Jerry", DialogLayout.Bottom)
        } else {
        	
        }
    }
})
